macros.command:
if (!token) {
    ui.notifications.error("Must select token");
    return;
}
const isNormal = token.document.ring.effects === 1;

const effects = isNormal ? 17 : 1;

await token.document.update({ring: {effects}})

const party = game.actors.party.members.filter(c => c.type === 'character')
const partyLevel = party.reduce(function (avg, member, _, { length }) {
        return avg + member.level / length;
    }, 0);
// Roll the d10 publicly
const roll = new Roll("1d10");
const rollResult = await roll.roll({async: true});
await rollResult.toMessage({flavor: "Rolling for Random Encounter"});

// Define the threat levels and their corresponding level adjustments
let threatLevels = [
  {range: [1], threat: "Trivial", adjustment: -2},
  {range: [2, 3], threat: "Low", adjustment: -1},
  {range: [4, 5, 6, 7], threat: "Moderate", adjustment: 0},
  {range: [8, 9], threat: "Severe", adjustment: 1},
  {range: [10], threat: "Extreme", adjustment: 2}
];

// Determine the threat level and adjustment
const threatLevel = threatLevels.find(t => t.range.includes(rollResult.total));

const adjustedLevel = partyLevel + threatLevel.adjustment;

const table = game.tables.getName('AV:E - Random Encounters');

if (table.getResultsForRoll(adjustedLevel).length === 0) {
    ChatMessage.create({content: "<p>You encountered nothing...</p>"})
}

await table.draw({roll: new Roll(`${adjustedLevel}`)})
